import React, { Component } from "react";
import "./App.css";
import { Table } from "react-bootstrap";
import Card from "./components/Card";

const URL =
  "https://servicenow-ui-coding-challenge-api.netlify.app/.netlify/functions/server/incidents";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      incidents_all: [],
      open: [],
      inprogress: [],
      resolved: [],
      closed: []
    };
  }
  async componentDidMount() {
    const res = await fetch(URL);
    const api = await res.json();
    const incidents_all = api;

    if (!resp.ok) {
      // get error message from body or default to response statusText
      const error = (incidents_all && incidents_all.message) || res.statusText;
      return Promise.reject(error);
    } else {
      let open = 0,
        inprogress = 0,
        resolved = 0,
        closed = 0;

      api.forEach(element => {
        if (element.state === "Open") {
          open++;
        } else if (element.state === "In Progress") {
          inprogress++;
        } else if (element.state === "Resolved") {
          resolved++;
        } else if (element.state === "Closed") {
          closed++;
        }
      });
      console.log("open", open);

      this.setState({
        incidents_all,
        open: open,
        inprogress: inprogress,
        resolved: resolved,
        closed: closed
      });
    }
  }
  render() {
    if (this.state.incidents_all == null) return <div />;
    let displayrows = [];
    let item = this.state.incidents_all;

    for (let i = 0; i < item.length; i++) {
      let temp_row_element = (
        <tr>
          <td>{item[i].number}</td>
          <td>{item[i].priority}</td>
          <td>{item[i].short_description}</td>
          <td>{item[i].category}</td>
          <td>{item[i].state}</td>
          <td>{item[i].sys_created_on}</td>
        </tr>
      );
      displayrows.push(temp_row_element);
    }
    return (
      <div className="content">
        <button onClick={() => this.getDataByState("Open")}>
          By State Open {this.state.open}
        </button>
        <button onClick={() => this.getDataByState("In Progress")}>
          By State In Progress {this.state.inProgress}
        </button>
        <button onClick={() => this.getDataByState("Closed")}>
          By State Closed {this.state.closed}
        </button>
        <button onClick={() => this.getDataByState("Resolved")}>
          By State Resolved {this.state.resolved}
        </button>

        <h3>At A Glance</h3>
        <Card href="/" title={"aaa"} />
        <h3>All Incidents</h3>
        {this.incident_table(displayrows)}
      </div>
    );
  }
  incident_table = row_item => {
    return (
      <div class="incident_table">
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Number</th>
              <th>Priority</th>
              <th>Short description</th>
              <th>Category</th>
              <th>State</th>
              <th>Created</th>
            </tr>
          </thead>
          <tbody>{row_item}</tbody>
        </Table>
      </div>
    );
  };
  getDataByState = stateReqested => {
    fetch(URL + stateReqested).then(async res => {
      const api = await res.json();

      // check for error response
      if (!response.ok) {
        // get error message from body or default to response statusText
        const error = (data && data.message) || response.statusText;
        return Promise.reject(error);
      } else {
        console.log(data[0]);
        this.setState({ incidence_show: data });
      }
    });
  };
}

export default App;
