import React, { Component } from "react";
import "./App.css";
import { Table } from "react-bootstrap";
import Card from "./components/card";
import TableByState from "./components/table_by_state";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const URL =
  "https://servicenow-ui-coding-challenge-api.netlify.app/.netlify/functions/server";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      incidents_all: []
    };
  }
  async componentDidMount() {
    const res = await fetch(URL + "/incidents");
    const api = await res.json();
    const incidents_all = api;

    if (!res.ok) {
      // get error message from body or default to response statusText
      const error = (incidents_all && incidents_all.message) || res.statusText;
      return Promise.reject(error);
    } else {
      let open = 0,
        inprogress = 0,
        resolved = 0,
        closed = 0;

      api.forEach(element => {
        if (element.state === "Open") {
          open++;
        } else if (element.state === "In Progress") {
          inprogress++;
        } else if (element.state === "Resolved") {
          resolved++;
        } else if (element.state === "Closed") {
          closed++;
        }
      });
      console.log("open", open);

      this.setState({
        incidents_all,
        open: open,
        inprogress: inprogress,
        resolved: resolved,
        closed: closed
      });
    }
  }
  render() {
    return (
      <div className="content">
        <Router>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/open">Open</Link>
              </li>
              <li>
                <Link to="/inprogress">In Progress</Link>
              </li>
              <li>
                <Link to="/closed">Closed</Link>
              </li>
              <li>
                <Link to="/resolved">Resolved</Link>
              </li>
            </ul>

            <hr />
            <Switch>
              <Route exact path="/">
                <Home />
              </Route>
              <Route path="/open">
                <TableByState state={"Open"} />
              </Route>
              <Route path="/inprogress">
                <TableByState state={"In Progress"} />
              </Route>
              <Route path="/closed">
                <TableByState state={"Closed"} />
              </Route>
              <Route path="/resolved">
                <TableByState state={"Resolved"} />
              </Route>
            </Switch>
          </div>
        </Router>
        {/* <h3>At A Glance</h3>
        <div
          onClick={() => {
            this.get_data_by_state("Open");
          }}
        >
          <Card title={"State Open"} body={this.state.open} />
        </div>
        <div
          onClick={() => {
            this.get_data_by_state("In Progress");
          }}
        >
          {" "}
          <Card title={"State In Progress"} body={this.state.inprogress} />
        </div>
        <div
          onClick={() => {
            this.get_data_by_state("Closed");
          }}
        >
          {" "}
          <Card title={"State Resolved"} body={this.state.closed} />
        </div>
        <div
          onClick={() => {
            this.get_data_by_state("Resolved");
          }}
        >
          {" "}
          <Card title={"State Open"} body={this.state.resolved} />
        </div> */}
      </div>
    );
  }
}
class Home extends Component {
  render() {
    if (this.props.state == null) {
      return <div />;
    }
    if (this.state.incidents_all == null) return <div />;
    let displayrows = [];
    let item = this.state.incidents_all;

    for (let i = 0; i < item.length; i++) {
      let temp_row_element = (
        <tr>
          <td>{item[i].number}</td>
          <td>{item[i].priority}</td>
          <td>{item[i].short_description}</td>
          <td>{item[i].category}</td>
          <td>{item[i].state}</td>
          <td>{item[i].sys_created_on}</td>
        </tr>
      );
      displayrows.push(temp_row_element);
    }
    return <div>{this.incident_table(displayrows)}</div>;
  }
  // display all incidents
  incident_table = row_item => {
    return (
      <div className="incident_table">
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Number</th>
              <th>Priority</th>
              <th>Short description</th>
              <th>Category</th>
              <th>State</th>
              <th>Created</th>
            </tr>
          </thead>
          <tbody>{row_item}</tbody>
        </Table>
      </div>
    );
  };
}
export default App;
