import React, { Component } from "react";
import "./App.css";
import { Table } from "react-bootstrap";
import Card from "./components/card";
import TableByState from "./components/table_by_state";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const URL =
  "https://servicenow-ui-coding-challenge-api.netlify.app/.netlify/functions/server";

class App extends Component {
  render() {
    return (
      <div className="content">
        <Router>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/open">Open</Link>
              </li>
              <li>
                <Link to="/inprogress">In Progress</Link>
              </li>
              <li>
                <Link to="/closed">Closed</Link>
              </li>
              <li>
                <Link to="/resolved">Resolved</Link>
              </li>
            </ul>

            <hr />
            <Switch>
              <Route exact path="/">
                <Home />
              </Route>
              <Route path="/open">
                <Open />
              </Route>
              <Route path="/inprogress">
                <Inprogress />
              </Route>
              <Route path="/closed">
                <Closed />
              </Route>
              <Route path="/resolved">
                <Resolved />
              </Route>
            </Switch>
          </div>
        </Router>
        {/* <h3>At A Glance</h3>
        <div
          onClick={() => {
